INSERT INTO `news` (`id`, `user_id`, `category_id`, `subcategory_id`, `tag_id`, `title`, `titleBn`, `titleEn`, `image`, `description`, `descriptionEn`, `descriptionBn`, `view_count`, `is_approved`, `status`, `created_at`, `updated_at`) VALUES
(2, 1, 2, NULL, NULL, 'মহামারীকালে ছাত্রলীগের ভূমিকার প্রশংসায় শেখ হাসিনা', NULL, NULL, 'uploads/2021-01/News_Photo/1609825120_sheikh-hasina-anniversary-bcl-040121-01.jpg', 'করোনাভাইরাস মহামারীকালে ছাত্রলীগের ভূমিকার প্রশংসা করে সংগঠনটির নেতা-কর্মীদের সমালোচনা উপেক্ষা করে কাজ করে যেতে বলেছেন আওয়ামী লীগ সভানেত্রী শেখ হাসিনা।\r\n\r\nছাত্রলীগের প্রতিষ্ঠাবার্ষিকী উপলক্ষে সোমবার আয়োজিত আলোচনা সভায় গণভবন থেকে ভিডিও কনফারেন্সে যুক্ত হয়ে বক্তব্য রাখেন প্রধানমন্ত্রী।\r\n\r\nমহামারী মোকাবেলায় ছাত্রলীগের নেতাকর্মীদের ভূমিকার প্রশংসা করে তিনি বলেন, “তোমরা যে কাজগুলো করে গেছ, তার জন্য সব সময় আমরা সাধুবাদ জানাই।\r\n\r\n“আমি জানি হয়ত ক্ষেত্রবিশেষে কোথাও কোথাও দুই-একটা ঘটনা ঘটে। আর আমাদের কিছু পত্র-পত্রিকা আছে, যতই ভালো কাজ কর, সেটা লেখার তাদের যোগ্যতা নেই। যদি কোথাও এতটুকু খুঁত পায়, সেটিকে বড় করে লিখতে পারে। এটা তাদের মন-মানসিকতার দৈন্য বলেই আমি মনে করি। কাজেই ওগুলো আমি বেশি একটা হিসেবে ধরি না।”\r\n\r\nতিনি বলেন, “আমি দেখি যে বৃক্ষরোপণ করা, ধান কেটে কৃষককে সহযোগিতা করা বা করোনাভাইরাসের সময়ে আক্রান্ত রোগী এবং যারা মৃত্যুবরণ করেছে তাদের পাশে দাঁড়ানো, তাদের সাহায্য করা, ঘরে ঘরে খাদ্য পৌঁছে দেওয়া, যখন ঝড় আসল, সেই ঝড়ের সময়ে মানুষের পাশে দাঁড়ানো, এই যে মানুষের সেবার জন্য যেই কাজগুলো করে যাচ্ছ, সেটাই হচ্ছে বড় কাজ।”', NULL, NULL, 0, 1, 1, '2021-01-04 23:38:40', '2021-01-04 23:38:40'),
(3, 1, 3, NULL, NULL, 'সরকারি সহায়তার ‘ভিক্ষুক’ তালিকায় স্ত্রী-মেয়ে: সেই নেতাকে সমাজসেবা সম্মাননা', NULL, NULL, 'uploads/2021-01/News_Photo/1609825254_b-baria-040121-01.jpg', 'ভিক্ষুক-দরিদ্রদের জন্য সরকারি সহায়তার তালিকায় স্ত্রী, মেয়েসহ স্বজনদের নাম দেওয়া ব্রাহ্মণবাড়িয়ার সেই আওয়ামী লীগ নেতা মো. শাহ আলমকে সম্মাননা দিয়েছে জেলা সমাজসেবা কার্যালয়।\r\n\r\nসমাজসেবা দিবস ২০২১ উপলক্ষে গত শনিবার জেলা সার্কিট হাউসে এক অনুষ্ঠানে জেলা প্রশাসক হায়াত-উদ-দৌলা খান তার হাতে সমাজসেবায় বিশেষ অবদানের এই সম্মাননা স্মারক তুলে দেন।\r\n\r\nপরে শাহ আলম নিজেই তার ফেইসবুক আইডি থেকে সম্মাননা পাওয়ার বিষয়ে পোস্ট দেন।\r\n\r\nপুরস্কার পাওয়া মো. শাহ আলম ব্রাহ্মণবাড়িয়া জেলা আওয়ামী লীগের শিল্প ও বাণিজ্য বিষয়ক সম্পাদক।\r\n\r\nকরোনাভাইস মহামারীতে ভিক্ষুকসহ অসহায়-গরিবদের জন্য সরকারের দেওয়া ওএমএস কার্ডে ভাগ বসিয়েছিলেন এই নেতা।\r\n\r\nবিষয়টি জানাজানি হলে ব্রাহ্মণবাড়িয়া জেলা প্রশাসক হায়াত-উদ-দৌলা খান ব্রাহ্মণবাড়িয়া সমাজ সেবা বিভাগের উপ-পরিচালকে ব্যাখ্যা তলব করেছেন।', NULL, NULL, 0, 1, 1, '2021-01-04 23:40:54', '2021-01-04 23:40:54'),
(4, 1, 4, NULL, NULL, 'আগেই শুকালো তিস্তা, কৃষকের কপালে চিন্তার ভাঁজ', NULL, NULL, 'uploads/2021-01/News_Photo/1609825606_rangpur-050121-01.jpg', 'তিস্তা নদীর পানি অন্যবারের তুলনায় আগেই শুকিয়ে যাওয়ায় রবি শস্যের আবাদ নিয়ে দুশ্চিন্তায় পড়েছেন দুই তীরের কৃষকরা। \r\n\r\nতারা বলছেন, সাধারণত ডিসেম্বর-জানুয়ারি পর্যন্ত নদীতে কিছু পানি থাকে, যা দিয়ে শীতকালীন আবাদ চলে। এবার ডিসেম্বরেই পানি শুকিয়ে তিস্তা ধু ধু করছে।\r\n\r\nবাধ্য হয়ে শীতকালীন ফসল আবাদ করতে নদীতে শ্যালো মেশিন বসাতে হচ্ছে তাদের। কুমড়া, পেঁয়াজ, রসুন, বাদাম আর আলু চাষে লাগছে বাড়তি শ্রম ও খরচ। শ্যালো মেশিনের সেচ দেওয়ার সংগতি যাদের নেই তারা দূর-দূরান্ত থেকে পানি বয়ে এনে দিচ্ছেন ক্ষেতে।\r\nকৃষকরা বলছেন, কয়েক দফা দফার বন্যা নদী তীরের জমিতে পলির মোটা আস্তরণ পড়ায় চাষাবাদ ব্যাহত হতে পারে। পলি যেমন জমির উর্বরতা বাড়ায়, তেমনি আবার অতিরিক্ত পলিও আবাদে বিরূপ প্রভাব ফেলে।', NULL, NULL, 4, 1, 1, '2021-01-04 23:46:46', '2021-01-20 23:38:51'),
(5, 1, 5, NULL, NULL, 'ভারতীয় পেঁয়াজের চাহিদা নেই হিলিতে', NULL, NULL, 'uploads/2021-01/News_Photo/1609833333_dddd.PNG', 'নিষেধাজ্ঞা প্রত্যাহারের পর ভারত থেকে আমদানি শুরু হওয়ায় দিনাজপুরের হিলি স্থলবন্দরের পাইকারি ও খুচরা বাজারে পেঁয়াজের দাম কমতে শুরু করেছে। কেজিপ্রতি পেঁয়াজের দাম কমেছে প্রকারভেদে ১০ থেকে ১৫ টাকা পর্যন্ত।\r\n\r\nদীর্ঘদিন পর দেশে ঢুকলেও ভারতীয় পেঁয়াজের তেমন ক্রেতা নেই। অপরদিকে দিনাজপুরের কাঁচাবাজারগুলোতে পেঁয়াজের দামে কোনো প্রভাব পড়েনি।\r\n\r\nঅভ্যন্তরীণ সংকট দেখিয়ে গেল বছরের ১৪ সেপ্টেম্বর থেকে সাড়ে ৩ মাস বাংলাদেশে পেঁয়াজ রফতানি বন্ধ করে দেয় ভারত সরকার। এ কারণে বাংলাদেশে পেঁয়াজ আমদানি বন্ধ হয়ে যায়। ভারতের কৃষি মন্ত্রণালয় পেঁয়াজ রফতানিতে নিষেধাজ্ঞা প্রত্যাহার করে নিয়ে গেল বছরের ২৮ ডিসেম্বর এ সংক্রান্ত একটি নিদের্শনা জারি করে ভারত সরকার। নিষেধাজ্ঞা প্রত্যাহারের পর গত শনিবার থেকে এ বন্দর দিয়ে দুই দিনে ১৩৩ মেট্রিক টন পেঁয়াজ আমদানি হয়েছে।\r\n\r\nপেঁয়াজ আমদানি শুরু হওয়ায় প্রভাব পড়েছে হিলির স্থানীয় বাজারে। গত শুক্রবার প্রতিকেজি দেশি পেঁয়াজ ৪০ থেকে ৪৫ টাকা দরে বিক্রি হলেও আজ সোমবার ৩০ থেকে ৩৫ টাকা কেজি দরে বিক্রি হচ্ছে। আর ভারত থেকে আমদানি করা পেঁয়াজ প্রতিকেজি বিক্রি হচ্ছে ২৭ থেকে ৩০ টাকা দরে।\r\n\r\nহিলি বন্দরের ব্যবসায়ী মিঠু মিয়া বলেন, আমদানি পুরোদমে শুরু হলে এক সপ্তাহের মধ্যে ভারতীয় পেঁয়াজের দাম আরও কমে যাবে।', NULL, NULL, 0, 1, 1, '2021-01-05 01:55:33', '2021-01-05 01:55:33'),
(6, 1, 6, NULL, NULL, 'প্রবাসী নারীকে ধর্ষণের অভিযোগে ইউপি চেয়ারম্যানের বিরুদ্ধে মামলা', NULL, NULL, 'uploads/2021-01/News_Photo/1609833500_abcc.PNG', 'প্রবাসী নারীকে ধর্ষণের অভিযোগে দিনাজপুরের বীরগঞ্জ উপজেলার ৬নং নিজপাড়া ইউনিয়ন পরিষদের আব্দুল খালেক সরকারের বিরুদ্ধে মামলা হয়েছে।\r\n\r\nবুধবার (২৩ ডিসেম্বর) সকালে ভুক্তভোগী ওই নারী বীরগঞ্জ থানায় মামলাটি করেন।\r\n\r\nমামলার এজাহার সূত্রে জানা গেছে, প্রথম স্বামীর সঙ্গে বিয়ে বিচ্ছেদ হওয়ার পর বাবার বাড়িতে অবস্থান করাকালে ২০১৫ সালের ঈদ উপলক্ষে চেয়ারম্যান আব্দুল খালেক সরকার সরকারি চাল দেয়ার জন্য ওই নারীকে ইউনিয়ন পরিষদে ডেকে নেন। তার কথামত ইউনিয়ন পরিষদে গেলে সেখানকার একটি পুরাতন ভবনে নিয়ে তাকে ধর্ষণ করেন চেয়ারম্যান।\r\n\r\nধর্ষণের বিষয়ে কাউকে না বলার জন্য হুমকি দেন এবং জানালে তার পরিবারের সবাইকে মেরে লাশ গুম করে দিবে বলে জানান চেয়ারম্যান। এভাবে ২০১৫ সাল থেকে ২০১৮ সাল পর্যন্ত ওই নারীকে বিভিন্ন স্থানে নিয়ে গিয়ে বিয়ের প্রলোভন দেখিয়ে ধর্ষণ করেন এবং ভিডিও চিত্র ধারণ করে রাখেন চেয়ারম্যান। এরপর ওই নারী কাজের জন্য সৌদি আরবে চলে যান।\r\n\r\nবিদেশে থাকা অবস্থায় চেয়ারম্যান আব্দুল খালেক সরকার সামাজিক যোগাযোগ মাধ্যমে বিভিন্নভাবে যৌন উত্তেজক কথাবার্তা বলতেন। দুই বছর পর সম্প্রতি ওই নারী সৌদি আরব থেকে দেশে ফেরত আসলে তার বাবা-মা একই উপজেলার এক ব্যক্তির সঙ্গে বিয়ে দিয়ে দেন। দেশে ফেরত আসা এবং অন্য ব্যক্তির সঙ্গে বিয়ের খবর শুনে চেয়ারম্যান খালেক সরকার মোবাইল ফোনে ওই নারীকে নানা ধরনের হুমকি দেন। পরে ভিডিওগুলো ফেরত দেয়ার কথা বলে দেখা করতে বলেন।\r\n\r\nতার কথামতো গত মঙ্গলবার দুপুর ২টার দিকে উপজেলার নোখাপাড়ায় চেয়ারম্যানের ইটভাটার পাশে মৃত দেলোয়ার হোসেনের ছেলে আব্দুর রশিদের বাসায় নিয়ে গিয়ে ধর্ষণ করেন ও মোবাইলে ভিডিও ধারণ করতে থাকে। এ সময় ওই নারী তার মোবাইলের মেমোরি খুলে নেন এবং চেঁচামেচি করলে চেয়ারম্যান ঘটনাস্থল থেকে পালিয়ে যান। পরে তিনি পরিবারের পরামর্শে চেয়ারম্যানের বিরুদ্ধে মামলা করেন।', NULL, NULL, 0, 1, 1, '2021-01-05 01:58:20', '2021-01-05 01:58:20'),
(7, 1, 7, NULL, NULL, 'ভারত থেকে ভ্যাকসিন রফতানিতে কোনো নিষেধাজ্ঞা নেই:  সেরাম', NULL, NULL, 'uploads/2021-01/News_Photo/1609834372_image-211743-1609773522.jpg', 'ভারতে অক্সফোর্ড-অ্যাস্ট্রাজেনেকার করোনাভাইরাসের টিকা উৎপাদনকারী প্রতিষ্ঠান সেরাম ইন্সটিটিউট জানিয়েছে, তাদের টিকা রফতানির ওপর কোন নিষেধাজ্ঞা নেই।\r\n\r\nসেরাম ইন্সটিটিউটের জনসংযোগ কর্মকর্তা মায়াঙ্ক সেন ব্রিটিশ সংবাদমাধ্যম বিবিসিকে জানিয়েছেন, ভ্যাকসিন রফতানিতে নিষেধাজ্ঞার যে খবর প্রকাশিত হয়েছে, তা পুরোপুরি সঠিক নয়। কারণ তাদের ভ্যাকসিন রফতানির ওপর কোন নিষেধাজ্ঞা নেই। তবে করোনা ভ্যাকসিন রফতানির অনুমোদন পেতে আরো কয়েকমাস পর্যন্ত সময় লাগবে বলেও জানান মায়াঙ্ক সেন।\r\n\r\nসেরাম ইনস্টিটিউটের কাছ থেকে বাংলাদেশ অক্সফোর্ড-অ্যাস্ট্রাজেনেকার ৫০ লাখ ডোজ টিকা আগামী মাসের শুরুতে পাবে বলে আশা করা হচ্ছে। প্রতি মাসে ৫০ লাখ ডোজ হিসেবে পুরো তিন কোটি টিকার জন্য অগ্রিম হিসেবে এরইমধ্যে ছয়শ কোটি টাকা সেরামের অ্যাকাউন্টে রবিবার জমা দেয়ার কথা জানিয়েছে বাংলাদেশ সরকার।', NULL, NULL, 0, 1, 1, '2021-01-05 02:12:52', '2021-01-05 02:12:52'),
(8, 1, 8, NULL, NULL, 'যুক্তরাষ্ট্রের \'নিষেধাজ্ঞার হুমকিতে\' ভারত', NULL, NULL, 'uploads/2021-01/News_Photo/1609834439_image-211914-1609825575.jpg', 'রাশিয়ার কাছ থেকে অত্যাধুনিক ক্ষেপণাস্ত্র এস-৪০০ কেনার প্রক্রিয়ায় আছে ভারত। এনিয়ে ভারত রাশিয়ার সঙ্গে কয়েক বিলিয়ন ডলারের চুক্তি করেছে। তবে সম্প্রতি এক রিপোর্টে বলা হয়েছে, রাশিয়ার কাছে এই ক্ষেপণাস্ত্র কেনার জন্য মার্কিন নিষেধাজ্ঞায় পড়তে পারে ভারত।\r\n\r\nমার্কিন কংগ্রেসের গবেষণা মূলক শাখা কংগ্রেসনাল রিসার্চ সার্ভিস (সিআরএস) এর সর্বশেষ রিপোর্টে জানিয়েছে, ভারত আরো প্রযুক্তি শেয়ার এবং সহ-উত্পাদন উদ্যোগের ব্যাপারে আগ্রহী। অন্যদিকে যুক্তরাষ্ট্র ভারতকে তার প্রতিরক্ষা নীতিতে সংস্কারের আহ্বান জানিয়েছে।\r\n\r\nতাই ভারতের এই ক্ষেপণাস্ত্র এস-৪০০কেনা মার্কিন নিষেধাজ্ঞা আইনের আওতায় দেশটি যুক্তরাষ্ট্রের নিষেধাজ্ঞায় পড়তে পারে।\r\n\r\nতবে সিআরএস\'র প্রতিবেদন মার্কিন সরকারি কোন প্রতিবেদন নয়। এই প্রতিবেদনগুলোর নিরপেক্ষ বিশেষজ্ঞদের দ্বারা তৈরি হয়।\r\n\r\nমার্কিন নিষেধাজ্ঞা আসতে পারে এমন সম্ভাবনা থাকা সত্ত্বেও ২০১৮ সালের অক্টোবরে ক্ষেপণাস্ত্র এস-৪০০ কেনার জন্য রাশিয়ার সঙ্গে পাঁচ বিলিয়ন ডলারের চুক্তি করে ভারত। এরমধ্যে ২০১৯ সালে চুক্তির জন্য প্রথম ধাপে ভারত প্রায় ৮০০ মিলিয়ন ডলার পরিশোধ করে।\r\n\r\nগত মাসে রাশিয়া জানায়, ভারতের সঙ্গে তাদের প্রতিরক্ষা চুক্তি চলমান। যার মধ্যে ক্ষেপণাস্ত্র এস-৪০০ ও আছে।', NULL, NULL, 0, 1, 1, '2021-01-05 02:13:59', '2021-01-05 02:13:59'),
(9, 1, 9, NULL, NULL, 'নতুন দায়িত্বে জ্যোতি', NULL, NULL, 'uploads/2021-01/News_Photo/1609835020_image-211886-1609798671.jpg', 'ছোট-বড় দুই পর্দাতে অভিনয় করে সফলতার ছাপ রেখেছেন অভিনেত্রী জ্যোতিকা জ্যোতি। নিজের মেধা আর অভিনয় মুন্সিয়ানায় জয় করেছেন দর্শকহূদয়। ২০০৫ সালে ‘আয়না’ চলচ্চিত্রের মাধ্যমে অভিষেক ঘটে তার। এরপর একে একে বেলাল আহমেদের ‘নন্দিত নরকে’, তানভীর মোকাম্মেলের ‘রাবেয়া’ ও ‘জীবনঢুলী’, আজাদ কালামের ‘বেদেনী’সহ বেশকিছু চলচ্চিত্রে অভিনয় করে প্রশংসিত হন এই অভিনেত্রী।\r\n\r\nএছাড়া তার নাটক ‘একটি ঝড়াক্রান্ত শেফালী গাছ’, ‘স্বপ্নবুনন’, ‘ভালোবাসার লাল পিঁপড়া’, ‘তিতির স্বপ্ন দেখার সাহস’ ও ‘মুক্তি’ নাটকগুলোও ছিল বেশ প্রশংসিত। তবে অনেকদিন পর্দায় সরব না থাকলেও জয় করে চলেছেন মানুষের হূদয়। অভিনয়ের পাশাপাশি রাজনীতিতেও এখন সক্রিয় তিনি। অভিনয় সফলতার ধারাবাহিকতা রাখছেন রাজনীতির মাঠেও। সেই ধারাবাহিকতায় এবার তার নামের সঙ্গে যোগ হলো নতুন একটি পরিচয়। আওয়ামীলীগের বন ও পরিবেশ বিষয়ক উপ-কমিটিতে সদস্য পদ পেলেন জ্যোতিকা জ্যোতি।\r\n\r\nউল্লেখ্য, বিনোদন অঙ্গনে পরিচিত মুখ জ্যোতিকা জ্যোতি ২০১৮ সালে ময়মনসিংহ-৩ (গৌরীপুর) আসন থেকে উপনির্বাচন এবং সংসদ নির্বাচনে মনোনয়নপত্র সংগ্রহ করেছিলেন জ্যোতি। কিন্তু টিকেট না পেলেও দলীয় প্রার্থীর পক্ষে বেশ সরব ছিলেন রাজনীতির মাঠে।', NULL, NULL, 0, 1, 1, '2021-01-05 02:23:40', '2021-01-05 02:23:40'),
(10, 1, 10, NULL, NULL, 'বাংলাদেশ দলে চার নতুন মুখ', NULL, NULL, 'uploads/2021-01/News_Photo/1609835090_image-211658-1609753101.jpg', 'ওয়েস্ট ইন্ডিজের বিপক্ষে ২৪ জনের প্রাথমিক দল ঘোষণা করেছে বিসিবি। দলে সম্ভাবনাময় কয়েকজন তরুণকে রাখা হয়েছে।\r\n\r\nতাদের মধ্যে রয়েছেন- টপঅর্ডার ব্যাটসম্যান পারভেজ হোসেন ইমন, বাঁহাতি ফাস্ট বোলার শরিফুল ইসলাম, শেখ মেহেদি হাসান, নাসুম আহমেদ।\r\n\r\nসোমবার দুপুরে ওয়েস্ট ইন্ডিজের বিপক্ষে টেস্ট ও ওয়ানডে সিরিজের প্রাথমিক দল ঘোষণা করে বিসিবি।\r\n\r\n১০ জানুয়ারি বাংলাদেশে আসবে ওয়েস্ট ইন্ডিজ ক্রিকেট দল। ২০ তারিখ শুরু হবে তিন ম্যাচের ওয়ানডে সিরিজ। দুই ম্যাচ হবে ২২ ও ২৫ তারিখ। আর টেস্ট সিরিজ শুরু হবে ৩ ফেব্রুয়ারি। দ্বিতীয় ম্যাচ হবে ১১ ফেব্রুয়ারি।\r\n\r\n২০ জনের প্রাথমিক টেস্ট দল :\r\n\r\nমুমিনুল হক (অধিনায়ক), তামিম ইকবাল, সাকিব আল হাসান, নাজমুল হোসেন শান্ত, মুশফিকুর রহিম, মোহাম্মদ মিঠুন, লিটন দাস, ইয়াসির আলী চৌধুরী, মোহাম্মদ সাইফ হাসান, আবু জায়েদ রাহি, তাসকিন আহমেদ, সৈয়দ খালেদ আহমেদ, হাসান মাহমুদ, মোস্তাফিজু রহমান, মেহেদি হাসান মিরাজ, তাইজুল ইসলাম, নুরুল হাসান সোহান, সাদমান ইসলাম, নাইম হাসান এবং এবাদত হোসেন।\r\n\r\nওয়ানডের প্রাথমিক দল :\r\n\r\nতামিম ইকবাল (অধিনায়ক), সাকিব আল হাসান, নাজমুল হোসেন শান্ত, মুশফিকুর রহিম, মোহাম্মদ মিঠুন, লিটন দাস, মাহমুদউল্লাহ রিয়াদ, আফিফ হোসেন ধ্রুব, মোসাদ্দেক হোসেন সৈকত, সৌম্য সরকার, ইয়াসির আলী চৌধুরী, নাইম শেখ, তাসকিন আহমেদ, মোহাম্মদ আল-আমিন হোসেন, মোহাম্মদ শরিফুল ইসলাম, হাসান মাহমুদ, সাইফউদ্দিন, মোস্তাফিজুর রহমান, মেহেদি হাসান মিরাজ, তাইজুল ইসলাম, নাসুম আহমেদ, মোহাম্মদ পারভেজ হোসেন ইমন; মাহাদি হাসান এবং রুবেল হোসেন।', NULL, NULL, 0, 1, 1, '2021-01-05 02:24:50', '2021-01-05 02:24:50'),
(11, 1, 11, NULL, NULL, 'ডিলিট হওয়া ছবি ও নম্বর ফিরে পাওয়ার উপায়', NULL, NULL, 'uploads/2021-01/News_Photo/1609835222_Capturasffae.PNG', 'অনেক সময় ভুলবশত ফোন থেকে গুরুত্বপূর্ণ ছবি বা ফোন নম্বর ডিলিট হয়ে যায়। এ সময় করণীয় কী? তা জানেন না অনেকেই। ফোন থেকে মুছে যাওয়া ছবি বা নম্বর পুনরুদ্ধার করা বেশি কঠিন কাজ নয়। চাইলেই সহজে ফিরে পাওয়া সম্ভব। চলুন তবে জেনে নেওয়া যাক, কীভাবে উদ্ধার করবেন ছবি ও নম্বর-\r\n\r\nবর্তমানে মোবাইল ফোনের হারিয়ে যাওয়া তথ্য, ছবি ইত্যাদি উদ্ধারে নতুন অনেক অ্যাপ বের হয়েছে। গুগল প্লে স্টোরে খুঁজলেই পাওয়া যায় এসব অ্যাপ। এগুলোর যেকোনো একটি অ্যাপ ডাউনলোড করলেই উদ্ধার করা সম্ভব ছবি বা নম্বর।\r\n\r\nছবি উদ্ধারে করণীয়: যারা অ্যান্ড্রয়েড ফোন ব্যবহার করেন, তাদের ডিভাইসগুলোর মুছে ফেলা ছবি ও ভিডিও উদ্ধার করতে প্লে স্টোর থেকে ডিলিটেড ফটো রিকভারির মতো একটি থার্ড পার্টি অ্যাপ ডাউনলোড করুন। এরপর অ্যাপটিতে থাকা স্ক্যান অপশন ব্যবহার করে মুহূর্তেই আপনার ছবি বা নম্বর খুঁজে পেতে পারেন।\r\n\r\nআইফোন ব্যবহারকারীরা কাজটি আরও সহজেই করতে পারবেন। আইফোনের সর্বশেষ সংস্করণটির ফটো অ্যাপে ডিলিটেড ফটো নামে আলাদা একটি ফোল্ডারই রয়েছে। যেখানে সাম্প্রতিক সময়ে ডিলিট করা ছবিগুলো একমাস পর্যন্ত সংরক্ষিত থাকে। তবে যদি স্থায়ীভাবে কিছু মুছে ফেলেন, সেক্ষেত্রে হারিয়ে ফেলা তথ্য উদ্ধার করতে আপনাকে আই ক্লাউড বা আই টিউন্স ব্যবহার করতে হবে।\r\n\r\nকন্ট্যাক্ট ও টেক্সট উদ্ধার: হারিয়ে যাওয়া কন্ট্যাক্ট লিস্ট, টেক্সট এমনকি আপনার মোবাইল থেকে মুছে যাওয়া ছবিগুলো উদ্ধার করতে অ্যান্ড্রয়েড ডাটা রিকভারি অ্যাপটি ব্যবহার করতে পারেন। সব ধরনের অ্যান্ড্রয়েড ডিভাইসেই এটি কার্যকরী। গুগল প্লে স্টোর থেকে অ্যাপটি ডাউনলোড করে মুছে যাওয়া নম্বরগুলো পুনরায় ফিরে পেতে পারেন।\r\n\r\nঅন্যদিকে আপনি যদি আইফোন ব্যবহারকারী হন, তবে কন্ট্যাক্ট লিস্ট এবং টেক্সট ফিরে পেতে আই টিউন ব্যবহার করুন। আই টিউনে ঢুকেই উপরের ডানদিকের কোণায় ক্লিক করুন। এরপর ব্যাকআপ অপশনটিতে ক্লিক করুন। এরপর দেখবেন, আপনার আইওএস ডিভাইসটিতে আইটিউনে ব্যাকআপ হিসেবে থাকা সব কন্ট্যাক্ট লিস্ট এবং টেক্সটগুলো উদ্ধার হবে।', NULL, NULL, 0, 1, 1, '2021-01-05 02:27:02', '2021-01-05 02:27:02'),
(12, 1, 3, NULL, NULL, 'খবর > করোনাভাইরাস মহামারী  করোনাভাইরাস: এক দিনে শনাক্ত ৯৯১ রোগী', NULL, 'Bangladesh  Bangladesh reports 991 new virus cases, another 20 die', 'uploads/2021-01/News_Photo/1609841992_coronavirus-test-suhrawardy-medical-181220-02.jpg', 'দেশে করোনাভাইরাসে আক্রান্ত হয়ে গত এক দিনে আরও ২০ জনের মৃত্যু হয়েছে, নতুন রোগী শনাক্ত হয়েছে আরও ৯৯১ জন।\r\n\r\nমঙ্গলবার বিকালে সংবাদমাধ্যমে বিজ্ঞপ্তি পাঠিয়ে স্বাস্থ্য অধিদপ্তরের পক্ষ থেকে দেশে করোনাভাইরাস পরিস্থিতির এই সবশেষ তথ্য জানানো হয়।\r\n\r\nসেখানে বলা হয়, সকাল ৮টা পর্যন্ত শনাক্ত ৯৯১ জনকে নিয়ে দেশে করোনাভাইরাসে মোট আক্রান্তের সংখ্যা বেড়ে ৫ লাখ ১৭ হাজার ৯২০ জন হয়েছে।\r\n\r\nআর গত এক দিনে মারা যাওয়া ২০ জনকে নিয়ে দেশে করোনাভাইরাসে মোট মৃতের সংখ্যা ৭ হাজার ৬৭০ জনে দাঁড়িয়েছে।\r\n\r\nস্বাস্থ্য অধিদপ্তরের হিসাবে বাসা ও হাসপাতালে চিকিৎসাধীন আরও ৯৪৪ হাজার জন রোগী সুস্থ হয়ে উঠেছেন গত এক দিনে। তাতে এ পর্যন্ত সুস্থ রোগীর মোট সংখ্যা বেড়ে ৪ লাখ ৬২ হাজার ৪৫৯ জন হয়েছে।\r\n\r\nবাংলাদেশে করোনাভাইরাসের প্রথম সংক্রমণ ধরা পড়েছিল গত ৮ মার্চ; তা ৫ লাখ পেরিয়ে যায় ২০ ডিসেম্বর। এর মধ্যে গত ২ জুলাই ৪ হাজার ১৯ জন কোভিড-১৯ রোগী শনাক্ত হয়, যা এক দিনের সর্বোচ্চ শনাক্ত।', 'Bangladesh has recorded 991 new cases of the novel coronavirus in a daily count, taking the tally of infections so far to 517,920.\r\n\r\nThe death toll climbed to 7,670 after 20 fatalities were registered in the 24 hours to 8 am Tuesday, according to data released by the government.\r\n\r\nAnother 944 patients recovered from the illness through treatment at home and in hospital care in the same period, bringing the total to 462,459.\r\n\r\nAs many as 14,462 samples were tested at 180 authorised labs across the country in the last 24 hours, yielding a positivity rate of 6.85 percent.', NULL, 0, 1, 1, '2021-01-05 04:19:52', '2021-01-05 04:19:52'),
(13, 1, 2, NULL, NULL, 'করোনাভাইরাস: এক দিনে ১৭ মৃত্যু, ৯৭৮ রোগী শনাক্ত', NULL, 'Bangladesh reports 978 virus cases, 17 deaths in a day', 'uploads/2021-01/News_Photo/1609930183_holiday-market-motijheel-181220-07.jpg', 'দেশে করোনাভাইরাসে আক্রান্ত হয়ে দেশে গত এক দিনে আরও ১৭ জনের মৃত্যু হয়েছে, নতুন রোগী শনাক্ত হয়েছে আরও ৯৭৮ জন।\r\n\r\nবুধবার বিকালে সংবাদমাধ্যমে বিজ্ঞপ্তি পাঠিয়ে স্বাস্থ্য অধিদপ্তরের পক্ষ থেকে দেশে করোনাভাইরাস পরিস্থিতির এই সবশেষ তথ্য জানানো হয়।\r\n \r\nসেখানে বলা হয়, সকাল ৮টা পর্যন্ত শনাক্ত ৯৭৮ জনকে নিয়ে দেশে করোনাভাইরাসে মোট আক্রান্তের সংখ্যা বেড়ে ৫ লাখ ১৮ হাজার ৮৯৮ জন হয়েছে।\r\n \r\nআর গত এক দিনে মারা যাওয়া ১৭ জনকে নিয়ে দেশে করোনাভাইরাসে মোট মৃতের সংখ্যা ৭ হাজার ৬৮৭ জনে দাঁড়িয়েছে।', 'Bangladesh has recorded 978 new cases of the novel coronavirus in a daily count, taking the tally of infections so far to 518,898.\r\n\r\nThe death toll climbed to 7,687 after 17 fatalities were registered in the 24 hours to 8 am Wednesday, according to data released by the government.\r\n\r\nThe number of fresh recoveries outpaced active cases in the same period as another 1,021 patients recovered from the illness through treatment at home and in hospital care, bringing the total to 463,480.\r\n\r\nAs many as 15,544 samples were tested at 181 authorised labs across the country in the last 24 hours, yielding a positivity rate of 6.29 percent.', NULL, 0, 1, 1, '2021-01-06 04:49:43', '2021-01-06 04:49:43'),
(14, 1, 11, NULL, NULL, 'সোলার উইন্ডস হ্যাকিং: রাশিয়াকেই দুষছেন মার্কিন গোয়েন্দারা', NULL, 'Home solar is growing, but big installers are still losing money', 'uploads/2021-01/News_Photo/1609943336_solar-panels-050121-01.jpg', 'ডিসেম্বরে গুরুতর সাইবার হামলার কবলে পড়েছিল যুক্তরাষ্ট্রের একাধিক রাষ্ট্রীয় সংস্থা। ওই হামলার পেছনে রাশিয়ার হাত রয়েছে বলে রায় দিয়েছে মার্কিন গোয়েন্দা সংস্থার টাস্ক ফোর্স।\r\n\r\nমার্কিন প্রেসিডেন্ট ডনাল্ড ট্রাম্প ওই সময়ে হ্যাকিংয়ের জন্য চীনকে দায়ী করলেও তার প্রশাসনের অনেকেই আঙুল তুলেছিলেন মস্কোর দিকে।\r\n\r\nএক যৌথ বিবৃতিতে গোয়েন্দা সংস্থার টাস্ক ফোর্স জানিয়েছে, দশটির মতো সরকারি সংস্থা এবং আরও কয়েকটি বেসরকারি কাঠামো ওই সাইবার হামলায় ডেটা হারিয়েছে। সংস্থাগুলো আরও জানিয়েছে, হামলার উদ্দেশ্য জানার লক্ষ্যে এখনও তদন্ত চলছে। দেখে মনে হচ্ছে, তথ্য সংগ্রহ করাই মূল লক্ষ্য ছিল হ্যাকারদের।\r\n\r\nবিবিসি এক প্রতিবেদনে উল্লেখ করেছে, ডিসেম্বরের সাইবার হামলার ব্যাপারটি খতিয়ে দেখতে এফবিআই, এনএসএ এর মতো সংস্থার মিশেলে ‘সাইবার ইউনিফাইড কোঅর্ডিনেশন গ্রুপ’ নামে একটি টাস্ক ফোর্স গঠন করা হয়েছিল। সাম্প্রতিক বিবৃতিটি ওই টাস্ক ফোর্সের পক্ষ থেকেই এসেছে।\r\n\r\nসাইবার নিরাপত্তা প্রতিষ্ঠান সোলার উইন্ডসের ১৮ হাজার ‘অরিয়ন’ সেবাগ্রহীতা সাইবার হামলার কবলে পড়েছিলেন। কিন্তু গোয়েন্দা সংস্থার বিশ্বাস, ১৮ হাজার ভুক্তভোগীর স্বল্প সংখ্যকই ওই হামলার শিকার হয়েছে। সাইবার হামলার ভুক্তভোগীদের মধ্যে মার্কিন ট্রেজারারি-ও ছিল।', 'The home solar business is growing fast as thousands of homeowners install panels on their roofs to save money. Yet the biggest companies that install and finance home solar systems are reporting hundreds of millions of dollars in losses.\r\n\r\nThose losses are an ominous reminder of how hard it can be to make money in an industry widely viewed by political leaders and business executives as an important part of the global effort to address climate change. Solving this problem could help determine whether the use of residential solar power is quickly and widely adopted.\r\n\r\n“There have been very few success stories,” said Vikram Aggarwal, founder and chief executive of EnergySage, which helps consumers compare solar installers. “Practically everyone who has tried this has failed. The road is littered with dead bodies.”\r\n\r\nSunrun and Sunnova, two of the nation’s biggest home solar companies, lost a combined $500 million in the first nine months of the year, and their operations and purchases of solar systems collectively used up $1.3 billion in cash. The companies and their Wall Street supporters say the losses are occurring because solar installations are growing rapidly and require a lot of upfront investment, and because investors in the companies get to use the losses to offset their tax liabilities. Still, residential solar is a fiercely competitive and expensive business, and Sunrun and Sunnova must contend with hundreds of smaller rivals, many of which have been turning a profit for years.', NULL, 1, 1, 1, '2021-01-06 08:28:56', '2021-01-20 01:11:48'),
(15, 1, 2, NULL, NULL, 'বীরগঞ্জ পৌরসভা নির্বাচনে মেয়র পদে ত্রি-মুখী লড়াইয়ের সম্ভাবনা', NULL, NULL, 'uploads/2021-01/News_Photo/1610123609_IMG_20210105_185922-660x330.jpg', 'দ্বিতীয় ধাপে আগামী ১৬ জানুয়ারি -২০২১ দিনাজপুরের বীরগঞ্জ পৌরসভায় ভোটগ্রহণ অনুষ্ঠিত হবে।ভোটাররা পৌরসভার ৯টি ভোটকেন্দ্রের সবগুলোতে ইভিএম এর মাধ্যমে ভোট প্রদান করবেন। ভোটের প্রচার-প্রচারণা নিয়ে ব্যস্ত প্রার্থী ও সমর্থনকারী নেতা-কর্মীদের পদচারণায় মুখরিত হয়ে উঠেছে পুরো পৌরসভা এলাকা। উপজেলা নির্বাচন অফিস কার্যালয় সুত্রে জানা যায়, মেয়র পদে ৫জন প্রার্থী অংশগ্রহন করছেন। স্বতন্ত্র প্রার্থী হিসেবে বর্তমান মেয়র মো: মোশারফ হোসেন বাবুল (মোবাইল ফোন), বাংলাদেশ আওয়ামীলীগের প্রার্থী উপজেলা আওয়ামীলীগের ভারপ্রাপ্ত সাধারন সম্পাদক মো: নুর ইসলাম (নৌকা), স্বতন্ত্র প্রার্থী ও সাবেক মেয়র মোহাম্মদ হানিফ (জগ), পৌর যুবদলের সদস্য সচিব মো: মোকারম হোসেন (ধানের শীষ) ও ইসলামিক আন্দোলন বাংলাদেশের মো: শাহ আলম (হাত পাখা) মার্কা পেয়ে প্রচার প্রচারণা শুরু করেছেন। তবে এবারের বীরগঞ্জ পৌরসভার নির্বাচনে ত্রিমুখী লড়াইয়ের সম্ভাবনা রয়েছে। সাধারণ ভোটারদের অধিকাংশের মতে মোবাইল ফোন, জগ ও নৌকা প্রতীকের প্রার্থীদের মধ্যে যে কেউ একজন হবেন আগামীর পৌর মেয়র কিংবা পৌর পিতা। বাকী দুইজনের মধ্যে ধানের শীষ প্রতীকের মেয়র পদপ্রার্থীকে প্রচারে মাঠে থাকতে দেখা গেলেও হাতপাখা প্রতীকের মেয়র পদপ্রার্থীর তেমন একটা প্রচার প্রচারণা নেই বললেই চলে।\r\n\r\n১নং ওয়ার্ডে বর্তমান কাউন্সিলর মো: আব্দুল বারিক (টেবিল ল্যাম্প), কৃষ্ণ চন্দ্র বর্মণ (পাঞ্জাবি), মো: মুরাদ হোসেন (ডালিম) ও মোঃ মোশাররফ হোসেন (উটপাখি)। ভোটকেন্দ্রের নাম বীরগঞ্জ সরকারি কলেজ, ভোটার ১৬২২ জন।\r\n\r\n২নং ওয়ার্ডে বর্তমান কাউন্সিলর আহাম্মদ আলী (টেবিল ল্যাম্প), মোঃ আশরাফুল আলম ফুলি(উটপাখি), সৈয়দ সুলতান মাহমুদ (ডালিম), রিয়াদ মাহমুদ(পাঞ্জাবি), আবু ছাইদ (গাজর) ও মো: রুস্তম আলী (স্ত্রুু ড্রাইভার)। ভোটকেন্দ্রের নাম প্যারাডাইস কিন্ডারগার্টেন এন্ড স্কুল, ভোটার১৪৪২ জন।\r\n\r\n৩নং ওয়ার্ডে বর্তমান কাউন্সিলর মো: ওমর ফারুক (পাঞ্জাবি), মোঃ মোঃ আব্দুল আহাদ (পানির বোতল), মো: আপেল মাহমুদ (ডালিম), রশিদুল ইসলাম সরকার (উটপাখি)ও মো: কামরুল হাসান জুয়েল (টেবিল ল্যাম্প)। ভোটকেন্দ্র বীরগঞ্জ কেন্দ্রীয় সরকারি প্রাথমিক বিদ্যালয় ভোটার ১৫৬৬জন।\r\nআসন (১,২,৩) সংরক্ষিত ০১নং বর্তমান কাউন্সিলর মোছা: রহিমা খাতুন (চশমা), মোছা: সুমি (আনারস) ও মোছা: নিশাদ বানু (অটোরিক্সা)। সংরক্ষিত ভোটার ৪৬৩০ জন।', NULL, NULL, 0, 1, 1, '2021-01-08 10:33:29', '2021-01-08 10:33:29'),
(16, 1, 3, NULL, NULL, 'বীরগঞ্জ পৌরসভার ৫নং ওয়ার্ডে ডালিম প্রতীকের কাউন্সিলর মেহেদী হাসানের ব্যাপক গণসংযোগ', NULL, NULL, 'uploads/2021-01/News_Photo/1610123819_IMG_20210108_191934-660x330.jpg', 'রেজা মোঃ তৌফিক, স্টাফ রিপোর্টারঃ১৬ জানুয়ারী দিনাজপুরের বীরগঞ্জ পৌরসভার আসন্ন নির্বাচন ২০২১ইং উপলক্ষ্যে ৫নং ওয়ার্ডের ডালিম প্রতীকের কাউন্সিলর প্রার্থী মোঃ মেহেদী হাসান মেহেদী ৮ই জানুয়ারী শুক্রবার দিনব্যাপী ওয়ার্ডের বিভিন্ন মহল্লায় ব্যাপক গণসংযোগ করেন।\r\n\r\nমেহেদী হাসান মেহেদী এই ওয়ার্ডের মহল্লাগুলোর প্রতিটি বাড়ী বাড়ী গিয়ে ভোটারদের কাছে ভোট ও দোয়া প্রার্থনা করেন। তিনি তার নির্বাচনী ইশতেহারের মধ্যে যেসব প্রতিশ্রুতিগুলি দিয়েছেন, তার মধ্যে রয়েছে- ৫নং ওয়ার্ডবাসীর জন্ম-মৃত্যু সনদ, জাতীয়তা সনদ, নাগরিকত্ব, চারিত্রিক সনদ বিনামূল্যে প্রদান।\r\n\r\n৫ নং ওয়ার্ড আওতাভূক্ত সকল বৈদ্যুতিক খুঁটির লাইট দ্রুত সমাধানের ব্যবস্থা এবং প্রত্যেক খুঁটিতে হটলাইন ইমার্জেন্সী নম্বর চালু করা। ওয়ার্ডের সকল অচল ড্রেনেজ ব্যবস্থা অনতিবিলম্বে সচল করা এবং প্রত্যেকটি ড্রেন পরিস্কার-পরিচ্ছন্ন করে বন্ধ ড্রেনেজ ব্যবস্থা চালু, ডাস্টবিনের ময়লা-আবর্জনা দ্রুত অপসারনের ব্যবস্থা নেওয়া। ওয়ার্ডের অধীনে পানি সরবরাহ, লাইট ও পরিস্কার-পরিচ্ছন্নতা বজায় রাখা। ওয়ার্ডের সকল দুস্থ মানুষের বয়স্কভাতা, বিধবা ভাতা, প্রতিবন্ধী ভাতা, মাতৃত্বকালীন ভাতা অগ্রাধিকার ভিত্তিতে সুষ্ঠুভাবে বন্টন করা।\r\n\r\nগ্রহণ করা এবং আলোকিত ৫ নং ওয়ার্ড গড়ে তোলা। ওয়ার্ডের সকল রাস্তাঘাট, কালভার্ট, মসজিদ ও মন্দিরের উন্নয়নে ব্যবস্থা গ্রহণ করা। ওয়ার্ডের মশক নিধন কার্যকর, পোলিও টিকা এবং ভিটামিন এ ক্যাপসুল ক্যাম্পেইন নিয়মিতভাবে কার্যকর করা। উল্লেখ্য যে, ৫ নং ওয়ার্ডের কাউন্সিলর পদপ্রার্থী ও বর্তমান কাউন্সিলর মোঃ মেহেদী হাসান মেহেদী পরপর ২য় বারের মতো ওয়ার্ড কাউন্সিলর পদে নির্বাচিত হয়ে এই এলাকার উন্নয়নের পাশাপাশি সার্বক্ষণিক ভাবে সেবা দিয়ে সুনাম ও পরিচিতি লাভ করায় এবারে ৩য় বারের মতো কাউন্সিলর পদে নির্বাচনী প্রতিদ্বন্দ্বিতায় অংশগ্রহন করে প্রচার প্রচারনায় সাধারণ ভোটারদের ব্যাপক সাড়া পেয়েছেন। এ ব্যাপারে মতামত চাইলে ৫ নং ওয়ার্ডের অধিকাংশ ভোটাররাই তাদের অভিমতে জানান, মেহেদী হাসান এই ওয়ার্ডের জনগনের জন্য একজন চাহিদাসম্মত ও হেভিওয়েট প্রার্থী।', NULL, NULL, 0, 1, 1, '2021-01-08 10:36:59', '2021-01-08 10:36:59'),
(17, 1, 5, NULL, NULL, 'বীরগঞ্জ পৌরসভা নির্বাচনকে সামনে রেখে ব্যানার -ফেস্টুনে একাকার', NULL, NULL, 'uploads/2021-01/News_Photo/1610124194_received_436097987517614-660x330.jpeg', 'বিকাশ ঘোষ, স্টাফ রিপোর্টারঃ  নির্বাচন যত ঘুনিয়ে আসছে ততই প্রার্থীদের গণসংযোগ আর প্রচার-প্রচারণা জমে উঠেছে বীরগঞ্জ পৌরসভা নির্বাচন। আগামী ১৬ জানুয়ারি বীরগঞ্জ পৌরসভা নির্বাচনকে কেন্দ্র করে রাস্তাঘাট, অলিগলি বিভিন্ন ওয়ার্ডের পাড়া- মহল্লা এখন মিছিল ও স্লোগানমুখর হয়ে উঠেছে। ব্যানার -ফেস্টুনে ছেয়ে গেছে পুরো বীরগঞ্জ শহর ও আশেপাশে এলাকা। চায়ের দোকান থেকে শুরু করে বসতবাড়িতেও এখন আলোচনর বিষয় শুধু নির্বাচন। প্রার্থীরা ভোট চেয়ে চষে বেড়াচ্ছেন তাদের নির্বাচনী এলাকা। নির্বাচনে মেয়র আওয়ামী লীগ, বিএনপি,ইসলামী জোট মনোনীত, বিদ্রোহী ও স্বতন্ত্র প্রার্থী অংশ নিয়েছেন। আওয়ামী লীগ মনোনীত মেয়র প্রার্থী মোঃ নুর ইসলাম নুর (নৌকা) পেয়ে ব্যাপক গণসংযোগ করে বেড়াচ্ছেন। পাশাপাশি দলীয় নেতাকর্মী সাথে নিয়ে উঠান বৈঠক করে যাচ্ছে। অন্যদিকে আওয়ামী লীগ বিদ্রোহী প্রার্থী বর্তমান মেয়র মোঃ মোশারফ হোসেন বাবুল তিনিও (মোবাইল ফোন) প্রতীক নিয়ে ব্যাপক গণসংযোগ চালিয়ে যাচ্ছে। জয়ের ব্যাপারে শতভাগ আশা প্রকাশ করে সকল প্রার্থী। অন্যদিক স্বতন্ত্র প্রার্থী সাবেক মেয়র মাওলনা মোহাম্মদ হানিফ (জগ) ও বিএনপি মনোনীত প্রার্থী মো: মোকাররম হোসেন পলাশ (ধানের শীষ) দলীয় সমর্থক ও নেতাকর্মীদের প্রচার-প্রচাণায় থাকলেও ইসলামি জোট প্রার্থী শাহ আলমকে তেমন প্রচার-প্রচারণায় দেখা যাচ্ছে না।', NULL, NULL, 0, 1, 1, '2021-01-08 10:43:14', '2021-01-08 10:43:14'),
(18, 1, 6, NULL, NULL, 'বীরগঞ্জে কুয়েত স্যোসাইটি ফর রিলিফ বাংলাদেশের উদ্যোগে কম্বল বিতরণ', NULL, NULL, 'uploads/2021-01/News_Photo/1610124320_IMG_20210107_125333-660x330.jpg', 'রেজা মোঃ তৌফিক, স্টাফ রিপোর্টারঃদিনাজপুরের বীরগঞ্জ উপজেলার নিজপাড়া ইউনিয়নের গ্রামডাংঙ্গী প্রাথমিক বিদ্যালয় মাঠে বৃহস্পতিবার দুপুর ১২টায় কুয়েত স্যোসাইটি ফর রিলিফ বাংলাদেশ অফিসের উদ্যোগ ও সহায়তায় অত্র ইউনিয়নের দুঃস্থ – অসহায় শীতার্ত মানুষদের মাঝে ২ শতাধিক কম্বল বিতরণ করা হয়।\r\n\r\nকম্বল বিতরণ কালে উপস্থিত ছিলেন নিজপাড়া ইউনিয়ন পরিষদের সচিব মোঃ আতাউর রহমান, ডিজিটাল সেন্টারের উদ্যোক্তা আবু নাঈম, কুয়েত স্যোসাইটি ফর রিলিফ বাংলাদেশের সুপারভাইজার রুহুল আমীন, আব্দুল লতিফ,আঃ গোলাপ, বাবুল হোসেন, পিয়ারুল ইসলাম, সিরাজুল ইসলাম, ৭নং ওয়ার্ডের ইউপি সদস্য ওবায়দুল হক, গ্রামডাংঙ্গী সরকারী প্রাথমিক বিদ্যালয়ের প্রধান শিক্ষক আব্দুল লতিফ,নুর ইসলাম নুরু প্রমুখ।', NULL, NULL, 0, 1, 1, '2021-01-08 10:45:20', '2021-01-08 10:45:20'),
(19, 1, 7, NULL, NULL, 'বীরগঞ্জে ২ ক্লিনিকের ১৮হাজার টাকা জরিমানা', NULL, NULL, 'uploads/2021-01/News_Photo/1610124568_01-660x330 (1).jpg', 'দিনাজপুরের বীরগঞ্জ পৌরশহরের ২ ক্লিনিকে ১৮হাজার টাকা জরিমানা করেছে ভ্রাম্যমাণ আদালত। বুধবার বেলা সাড়ে ১১ টায় উপজেলা সহকারী কমিশনার (ভূমি) ও নির্বাহী ম্যাজিস্ট্রেট মো. ডালিম সরকার এর নেতৃত্বে এ অভিযান পরিচালনা করা হয়। অভিযানে সহযোগিতা করেন উপজেলা স্বাস্থ্য কমপ্লেক্সের মেডিকেল অফিসার ডা. আব্দুল্লাহ আল তানভীর তালুকদার চঞ্চল, উপজেলা স্যানিটারী ইন্সপেক্টর ফরিদ বিন ইসলাম সহ বীরগঞ্জ থানার পুলিশ ফোর্স। নির্বাহী ম্যাজিস্ট্রেট মো. ডালিম সরকার বীরগঞ্জ২৪কে জানান, অপরিষ্কার-অপরিচ্ছন্ন পরিবেশ, সরকারের নিয়ম অনুযায়ী মূল্য তালিকা না টাঙানো এবং নার্স, টেকনিশিয়ানদের প্রশিক্ষণের কাগজপত্র না থাকায় মেডিকেল প্র্যাকটিস এবং বেসরকারি ক্লিনিক ও ল্যাবরেটরি নিয়ন্ত্রণ অধ্যাদেশ ১৯৮২ এর ৯ ও ১৩ (২) ধারা মোতাবেক নূর ল্যাব এন্ড সিটি নাসিং হোমকে ১৫ হাজার টাকা অপরদিকে মর্ডাণ ক্লিনিককে নার্স ও টেকনিশিয়ানদের প্রশিক্ষণের কাগজপত্র এবং নিয়মিত ভাবে ডাক্তার না থাকায় ৩ হাজার টাকা জরিমানা আদায় করা হয়। উল্লেখ্য যে, ১১ আগস্ট ২০১৯ সালে ভ্রাম্যমান আদালতের পরিচালনার মাধ্যমে নূর ল্যাব এন্ড সিটি নাসিং হোমে ডাক্তার বিহীন আয়া দিয়ে বাচ্চা প্রসবকালে শিশু মৃত্যুর ঘটনা ও সরকারি ঔষধ রাখার অপরাধে ক্লিনিকটি সিলগালা করে দেওয়া সহ ক্লিনিক মালিক নুর আলম ও তার স্ত্রী বীরগঞ্জ উপজেলা স্বাস্থ্য কমপ্লেক্সের বর্তমানে বহিস্কৃত নার্স ফাহিমা আক্তারকে জেল জরিমানা করা হয়।', NULL, NULL, 1, 1, 1, '2021-01-08 10:49:28', '2021-01-20 23:39:20'),
(20, 1, 8, NULL, NULL, 'বাইডেনের জয় নারীদের ভোটে', NULL, NULL, 'uploads/2021-01/News_Photo/1610124701_Joe_Biden-2011051111_20201110154715.jpg', '৪৬ তম প্রেসিডেন্ট হিসেবে নির্বাচিত হয়েছেন ডেমোক্র্যাট প্রার্থী জো বাইডেন। তার এই বিজয়ের পেছনে সবচেয়ে বড় অবদান নারী ভোটারদের।\r\nওয়াল স্ট্রিট জার্নাল ও এনবিসি নিউজের এক যৌথ টেলিফোন জরিপের ফলাফলে দেখা গেছে, এবারের নির্বাচনে রিপাবলিকান পার্টির প্রার্থী এবং বর্তমান প্রেসিডেন্ট ডোনাল্ড ট্রাম্পের পক্ষে রায় দিয়েছে ৩৫ শতাংশ নারী এবং ৫০ শতাংশ পুরুষ। যেখানে বাইডেনের পক্ষে রায় দিয়েছে ৪৩ শতাংশ পুরুষ এবং ৫৮ শতাংশ নারী।\r\n\r\n১৯২০ সালে যুক্তরাষ্ট্রের নারীরা ভোটাধিকার পায়। তবে সেসময় তেমন বেশি ভোটই দিতে যাননি তারা। ১৯৮০ সালের পর থেকে তারা ভোট দেওয়ার প্রতি আগ্রহী হয়ে ওঠেন।  ক্যালিফোর্নিয়া ইন্টারন্যাশনাল ইউনিভার্সিটির রাজনীতি বিজ্ঞানের অধ্যাপক ডিপ্লাও গোউল্ড বলেন, ‘এখন নারীরাই ভোটের মাঠের জয়-পরাজয় নির্ধারণ করেন। ’\r\n\r\nঅ্যারিজোয়ানা অঙ্গরাজ্যের মারিকোপা কাউন্টির রিপাবলিকান কনসালটেন্ট লরনা রোমেরো জানান, রিপাবলিকান হিসেবে রেজিস্টার্ড নারীদের একটি বড় অংশের ভোট হারিয়েছেন ট্রাম্প। তিনি বলেন, এটা একজন প্রেসিডেন্টের আচরণ হতে পারে না। কমান্ডার-ইন-চিফের কাছ থেকে তারা এমন আচরণ প্রত্যাশা করেন না।\r\n\r\nটানটান উত্তেজনার অবসান ঘটিয়ে অবশেষে যুক্তরাষ্ট্রের প্রেসিডেন্ট নির্বাচিত হলেন জো বাইডেন। ভোটগ্রহণের চারদিন পর শনিবার পেনসিলভানিয়ার ২০ ইলেক্টোরাল ভোট পাওয়ায় জয় নিশ্চিত হয় তার। জয়ের জন্য প্রয়োজনীয় ২৭০ ইলেক্টোরাল কলেজ ভোটের চেয়ে ২০ ভোট বেশি পেয়েছেন বাইডেন। ৫৩৮ ইলেক্টোরাল ভোটের মধ্যে তিনি পেয়েছেন ২৯০ ভোট। প্রেসিডেন্ট ডোনাল্ড ট্রাম্প পেয়েছেন ২১৪ ভোট।', NULL, NULL, 0, 1, 1, '2021-01-08 10:51:41', '2021-01-08 10:51:41'),
(21, 1, 9, NULL, NULL, 'হুমায়ূন আহমেদের গান করবেন নন্দিতা', NULL, NULL, 'uploads/2021-01/News_Photo/1610125990_230151KK_RM_5-660x330.gif', 'বীরগঞ্জ২৪ ডেস্ক(Birganj24.com): প্রথম আলোর করোনাকালের আয়োজন ‘ঘরে বসে শোনাব গান’ অনুষ্ঠানে আজ গাইবেন নন্দিতা। রাত ১০টা ৩০ মিনিটে প্রথম আলোর ফেসবুক লাইভে উপভোগ করা যাবে তাঁর গান। হুমায়ূন আহমেদের মৃত্যুবার্ষিকীর এই দিনে নন্দিতা শোনাবেন হুমায়ূনের গান। সেগুলোর মধ্যে থাকবে ‘আমার ভাঙা ঘরের ভাঙা চালা’, ‘যে থাকে আঁখি পল্লবে’, ‘আমার আছে জল’, ‘চল না বৃষ্টিতে ভিজি’, ‘যদি মন কাঁদে’ গানগুলো।\r\n\r\nকরোনায় বিশ্বব্যাপী ঘরের ভেতর দিন কাটাচ্ছেন মানুষ। তাঁদের বিষণ্নতা দূর করতে এ আয়োজন করেছে প্রথম আলো। নিজ নিজ ঘরে বসে গান গেয়ে শ্রোতাদের মন ভালো করছেন বাংলাদেশি শিল্পীরা। শিল্পী নন্দিতা গানের প্রতিযোগিতা ‘বাংলাদেশি আইডল’–এ ২০১৩ সালে সেরা ৭-এ ছিলেন। গানের পাশাপাশি তিনি টেলিভিশনে গানের অনুষ্ঠান উপস্থাপনাও করেন।\r\n\r\nসানজিদা মাহমুদ নন্দিতা। ছবি: সংগৃহীত করোনাকালের আয়োজন ‘ঘরে বসে শোনাব গান’ অনুষ্ঠানে আজ গাইবেন নন্দিতা। রাত ১০টা ৩০ মিনিটে প্রথম আলোর ফেসবুক লাইভে উপভোগ করা যাবে তাঁর গান। হুমায়ূন আহমেদের মৃত্যুবার্ষিকীর এই দিনে নন্দিতা শোনাবেন হুমায়ূনের গান। সেগুলোর মধ্যে থাকবে ‘আমার ভাঙা ঘরের ভাঙা চালা’, ‘যে থাকে আঁখি পল্লবে’, ‘আমার আছে জল’, ‘চল না বৃষ্টিতে ভিজি’, ‘যদি মন কাঁদে’ গানগুলো।\r\n\r\nকরোনায় বিশ্বব্যাপী ঘরের ভেতর দিন কাটাচ্ছেন মানুষ। তাঁদের বিষণ্নতা দূর করতে এ আয়োজন করেছে প্রথম আলো। নিজ নিজ ঘরে বসে গান গেয়ে শ্রোতাদের মন ভালো করছেন বাংলাদেশি শিল্পীরা। শিল্পী নন্দিতা গানের প্রতিযোগিতা ‘বাংলাদেশি আইডল’–এ ২০১৩ সালে সেরা ৭-এ ছিলেন। গানের পাশাপাশি তিনি টেলিভিশনে গানের অনুষ্ঠান উপস্থাপনাও করেন।\r\n\r\n‘ঘরে বসে শোনাব গান’ অনুষ্ঠানে ইতিমধ্যে গান করেছেন শিল্পী তানভির আলম সজীব, রাহুল আনন্দ, প্রিয়াঙ্কা গোপ, কনক আদিত্য, ইমরান, কোনাল, পিন্টু ঘোষ, পুলক, পারভেজ, ঐশী, কিশোর, দিনাত জাহান মুন্নী, এস আই টুটুল, অদিতি মহসিন, বেলাল খান, সাব্বির, রন্টি দাস, হায়দার হোসেন, ডিরকস্টার শুভ, হৈমন্তী রক্ষিত দাশ, ইউসুফ আহমেদ খান, আনুশেহ আনাদিল, ফিডব্যাক, দুই ভাই হৃদয় ও প্রত্যয় খান, সমরজিৎ রায়, মেহরাব, ইউসুফ, শান, তপন চৌধুরী, ওয়ারদা আশরাফ, মুহিন খান, লাবিক কামাল গৌরব, অনিমা রায়, রাজীব, রাকিবা ইসলাম ঐশী, অপু আমান, স্বরলিপি, আশিকুজ্জামান টুলু, রোদিয়া, আশিক, মেহেদী হাসান, সন্দীপন, পূজা, রেহান, ঝিলিক, সুস্মিতা দে, মেহের আফরোজ শাওন, রাশেদ, সিঁথি সাহা ও অভিনেতা চঞ্চল চৌধুরী। ফেসবুক লাইভের পর এ অনুষ্ঠান দেখা যাবে প্রথম আলোর ফেসবুক পেজ ও ইউটিউব চ্যানেলে।', NULL, NULL, 0, 1, 1, '2021-01-08 11:13:10', '2021-01-08 11:13:10'),
(22, 1, 3, NULL, NULL, 'বীরগঞ্জে অভিমান করে দুই কিশোরী’র আত্মহত্যা', NULL, NULL, 'uploads/2021-01/News_Photo/1610126291_received_502608217380649-660x330.jpeg', 'দিনাজপুরের বীরগঞ্জে পৃথক পৃথক ঘটনায় মা ও ভাইয়ের সাথে অভিমান করে দুই কিশোরী আত্মহত্যা করেছে।\r\nবীরগঞ্জ থানার এস আই আবু হাসনাত জানান, ১৭ ডিসেম্বর বৃহস্পতিবার সকাল সাড়ে ৯টায় উপজেলার নিজপাড়া ইউনিয়নের প্রেমবাজার এলাকার মোঃ জিয়াউর রহমানের মেয়ে সানজিতা খানাম রিয়া (১৬) মায়ের সাথে অভিমান করে শয়ন ঘরে গলায় ওড়না পেছিয়ে আত্মহত্যা করেন। নিজপাড়া ইউনিয়নের চেয়ারম্যান এম এ খালেক সরকার বীরগঞ্জ থানা পুলিশকে অবগত করলে অফিসার ইনচার্জ আব্দুল মতিন প্রধানের নেতৃত্বে সঙ্গীয় পুলিশ ফোর্স ঘটনাস্থল পরিদর্শন করে ঘটনার সত্যতা নিশ্চিত করে লাশ পরিবারের কাছে হস্ততান্তর করেন। অন্যদিকে উপজেলার মোহাম্মদপুর ইউনিয়নের মাহানপুর – লষ্করপুর গ্রামের নরেন চন্দ্র রায়ের মেয়ে রূপশী (১৩) ভাইয়ের সাথে অভিমান করে গলায় রশি দিয়ে আত্মহত্যা করেন। এঘটনায় বীরগঞ্জ থানা পুলিশ পৃথকভাবে দুটি অপমৃত্যুর মামলা দায়ের করেছে।', NULL, NULL, 0, 1, 1, '2021-01-08 11:18:11', '2021-01-08 11:18:11'),
(23, 1, 14, NULL, NULL, 'করোনার ঝুঁকি নিয়েই গার্মেন্ট খুলছে আজ', NULL, NULL, 'uploads/2021-01/News_Photo/1610132353_IMG_20200426_103732-660x330.jpg', 'করোনা ভাইরাসের ঝুঁকি নিয়েই সীমিত আকারে তৈরি পোশাক খাতের কিছু কারখানা আজ খুলছে। শুরুতে ঢাকা মেট্রোপলিটন এলাকার পাশাপাশি নারায়ণগঞ্জের নিটওয়্যার খাতের কিছু কারখানা খুলবে।\r\n\r\nএরপর ধাপে ধাপে সাভার, গাজীপুরসহ অন্য এলাকার কারখানাও খুলবে। তবে যেসব কারখানার হাতে রপ্তানি আদেশ রয়েছে, সেসব কারখানাই প্রাথমিকভাবে খুলবে।\r\n\r\nসংশ্লিষ্ট সূত্র জানিয়েছে, কারখানা খোলার ক্ষেত্রে কী ধরনের স্বাস্থ্যবিধি মানতে হবে, তা নিয়ে পোশাকশিল্প মালিকদের সংগঠন বিজিএমইএ একটি গাইডলাইন তৈরি করেছে। ওই গাইডলাইন অনুযায়ী, দূরবর্তী এলাকা কিংবা ঢাকার বাইরে চলে যাওয়া শ্রমিকদের বাদ দিয়ে আপাতত কারখানার কাছাকাছি থাকা শ্রমিকদের দিয়ে উৎপাদন কাজ চালানো হবে।\r\n\r\nশর্ত সাপেক্ষে কারখানা খুলছে বলে জানিয়েছেন কলকারখানা প্রতিষ্ঠান ও পরিদর্শন অধিদপ্তরের মহাপরিদর্শক শিবনায় রায়। তিনি বলেন, কারখানা খুলতে উপযুক্ত সময়ের ব্যবধানে উদ্যোক্তারা আংশিক এবং ধাপে ধাপে খুলতে হবে। এ ছাড়া কঠোরভাবে স্বাস্থ্যবিধি মানার পরামর্শ দেওয়া\r\n\r\nহয়েছে। প্রাথমিকভাবে আজ রাজধানীর যেসব কারখানার রপ্তানি আদেশ আছে, সেগুলোর কয়েকটি খোলা হবে।\r\n\r\nবিজিএমইএর সহসভাপতি এম এ রহিম ফিরোজ বলেন, দেশের অর্থনীতি তো টিকিয়ে রাখতে হবে। অর্থনীতির স্বার্থে যথাযথ স্বাস্থ্যবিধি মেনে এবং পর্যায়ক্রমে অঞ্চলভিত্তিক অল্প শ্রমিক দিয়ে কারখানা খোলার দিকে যাচ্ছি আমরা। প্রধানমন্ত্রীও ধীরে ধীরে কারখানা সচল করার বিষয়ে ইতিবাচক মনোভাব দেখিয়েছেন।\r\n\r\nমালিকপক্ষ কারখানা চালুর সিদ্ধান্ত নিলেও শ্রমিকসংগঠনগুলো বর্তমান করোনাভাইরাসের ঊর্ধ্বমুখী পরিস্থিতিতে কারখানা চালু না করার পক্ষে। ইন্ডাস্ট্রিয়াল বাংলাদেশ কাউন্সিলের (আইবিসি) সাবেক মহাসচিব কুতুবউদ্দিন আহমেদ বলেন, আমাদের কাছে থাকা তথ্য অনুযায়ী, এরই মধ্যে দুই শতাধিক শ্রমিক করোনায় আক্রান্ত হয়েছে। এ পরিস্থিতিতে কারখানা খুললে ভয়াবহ বিপর্যয় ঘটতে পারে। কোনো ক্রমেই কারখানা খোলা উচিত হবে না।\r\n\r\nঅবশ্য এলাকাভিত্তিক কারখানা খোলার বিষয়ে বিভিন্ন তথ্য-উপাত্ত সংগ্রহ করছে শিল্পাঞ্চল পুলিশ। বিশেষত কোন এলাকা সবচেয়ে ঝুঁকিপূর্ণ—এ ধরনের তালিকা তৈরি করছে শিল্পাঞ্চল পুলিশ। শিল্পাঞ্চল পুলিশের একজন ঊর্ধ্বতন কর্মকর্তা বলেন, এখন পর্যন্ত গার্মেন্টসসহ বিভিন্ন খাতের দুই থেকে আড়াই শ শ্রমিক করোনায় আক্রান্ত হয়েছে। অবশ্য বিজিএমইএর পরিচালক ও তুসুকা গার্মেন্টের চেয়ারম্যান আরশাদ জামাল দীপু মনে করেন, গার্মেন্টে করোনায় আক্রান্ত শ্রমিকের সংখ্যা খুবই কম। তিনি বলেন, বেশিসংখ্যক শ্রমিক আক্রান্ত হলে নিশ্চয়ই এসব খবর চাপা থাকত না। তিনি বলেন, শ্রমিকদের কারখানায় ঢোকার ক্ষেত্রে থার্মাল স্ক্যানার দিয়ে জ্বর পরিমাপ করা, জীবাণুমুক্ত করা, কমসংখ্যক শ্রমিক দিয়ে কাজ চালানো এবং শ্রমিকদের মধ্যে প্রায় পাঁচ ফুট দূরত্ব নিশ্চিতসহ স্বাস্থ্যবিধি মানার ক্ষেত্রে বেশ কিছু উদ্যোগ নেওয়া হবে। এ ছাড়া শিল্পাঞ্চলগুলোতে শ্রমিকের করোনা পরীক্ষা করার বিষয়ে আলাদা ব্যবস্থার বিষয় নিয়েও আলোচনা চলছে। এমন পরিস্থিতির মধ্যেই গতকাল শনিবার কারখানা খোলাসহ সার্বিক পরিস্থিতি পর্যালোচনার লক্ষ্যে সংশ্লিষ্টদের সঙ্গে পরামর্শসভা করে ব্যবসায়ীদের শীর্ষ সংগঠন এফবিসিসিআই।', NULL, NULL, 0, 1, 1, '2021-01-08 12:59:13', '2021-01-08 12:59:13'),
(24, 1, 12, NULL, NULL, 'বীরগঞ্জে সড়ক দুর্ঘটনায় শিশুর মৃত্যু: ২৫ হাজারে মিটমাট, এলাকাবাসীর মানববন্ধন ও বিক্ষোভ মিছিল', NULL, NULL, 'uploads/2021-01/News_Photo/1610132500_photo-1-270x300.jpg', 'দিনাজপুরের বীরগঞ্জে সড়ক দুর্ঘটনায় শিশু নিহতের ঘটনায় ২৫ হাজার টাকায় স্থানীয় জনপ্রতিনিধি মিটমাট করায় কোন মামলা হয়নি থানায়। শিশুর মৃত্যুর ঘটনাকে কেন্দ্র করে মানববন্ধন সহ বিক্ষোভ মিছিল করেছেন এলাকাবাসী।\r\n\r\nউপজেলার ঝাড়বাড়ী-জয়গঞ্জ আত্রাই খেয়াঘাট পয়েন্টে বালুবাহী নছিমনের চাপায় ফারজানা আক্তার (৪) নামে এক শিশুর অকাল মৃত্যু হয়েছে। নিহত ফারজানা আক্তার ওই গ্রামের ফারুক হোসেনের মেয়ে।\r\n\r\nপ্রত্যক্ষদর্শীরা বলেন, সোমবার দুপুর ২ টায় নামা বলদিয়াপাড়া গ্রামে কাচা রাস্তায় ঝাড়বাড়ী-জয়গঞ্জ আত্রাই খেয়াঘাট পয়েন্ট থেকে বালু নিয়ে ঝাড়বাড়ী যাওয়ার পথে কুষ্টিয়া বডির সেলো মেশিন চালিত নছিমন চাপা দেয় ফারজানাকে। এতে ঘটনা স্থলে অকাল মৃত্যু হয় শিশুটির। দুর্ঘটনার পরেই জনসাধারন ঘাতক নছিমন সহ চালক কাশিমনগর গ্রামের রহিম উদ্দীনের ছেলে রফিকুলকে আটক করে রাখে। পরবর্তীতে ২৫ হাজার টাকায় মিটমাট হওয়ায় দুর্ঘটনাটি ধামাচাপা দেওয়ার চেষ্টা করছেন স্থানীয় জনপ্রতিনিধি।\r\n\r\nঅন্যদিকে সোমবার সন্ধ্যা সাড়ে ৬টায় চালকের শাস্তির দাবী সহ ড্রাম ট্রাক ও বালু মহল বন্ধের দাবীতে এলাকাবাসীদের আয়োজনে ঝাড়বাড়ী চৌরাস্তায় মানববন্ধন শেষে বিক্ষোভ মিছিল অনুষ্ঠিত হয়।\r\n\r\nমানববন্ধনে বক্তব্য রাখেন শতগ্রাম ইউনিয়ন আওয়ামী লীগের সাবেক সভাপতি সৈয়দ আতাউর রহমান, ৩নং ওয়ার্ডের সাবেক ইউপি সদস্য আলহাজ্ব আবু বক্কর সিদ্দিক, ৩নং ওয়ার্ড আওয়ামী লীগের সভাপতি ডাঃ এবি সিদ্দিক, শতগ্রাম ইউনিয়ন যুবলীগের সভাপতি হেলাল ফকির, সাধারণ সম্পাদক বিকাশ দেবনাথ, ঝাড়বাড়ী কলেজের প্রভাষক গোলাম রাব্বানী, ঝাড়বাড়ী উচ্চ বিদ্যালয়ের শিক্ষক তাসমি বারী,\r\nঝাড়বাড়ী প্রাথমিক বিদ্যালয়ে সহকারী শিক্ষক মোঃ মতিউল ইসলাম, ঝাড়বাড়ী পরিবেশ উন্নয়ন পরিষদের আহবায়ক শেখ মোঃ জাকির হোসেন ও অতঃপর ঝাড়বাড়ীর সভাপতি মেহেদী হাসান মুন, ছাত্রলীগ নেতা মোঃ আশরাফুল ইসলাম, মোঃ সাজু ইসলাম সহ আরো অনেকেই।\r\n\r\nমানববন্ধনে এলাকাবাসীরা বলেন , দীর্ঘদিন ধরে বালু মহলে এভাবে দ্রুত গতিতে গাড়ী চলাচলের ফলে দুর্ঘটনা যেমন ঘটছে তেমনি ঝাড়বাড়ী শান্তির মোড় থেকে বটতলা হয়ে আত্রাই নদী পর্যন্ত গ্রামের ছোট রাস্তায় বালু বোঝাই ভারি ৬ চাকা ও ১০ চাকার ড্রাম ট্রাক চলাচলের কারণে পাকা-কাচা সড়কগুলি ব্যাপকভাবে ক্ষতিগ্রস্ত হচ্ছে এবং সরকারি নির্দেশনা অমান্য করে রাতের-আঁধারে অপরিকল্পিত ও অবৈধ ভাবে বালুর গাড়ি চলাচলের ফলে রাস্তাঘাট এবং পরিবেশের ব্যাপক ক্ষতিসাধিত হচ্ছে। রাতের বেলা বালু পরিবহন আইনগত ভাবে নিষিদ্ধ হলেও সংশ্লিষ্ট ইজারাদার তা মানছে না।', NULL, NULL, 0, 1, 1, '2021-01-08 13:01:40', '2021-01-08 13:01:40'),
(25, 1, 15, NULL, NULL, 'রাতে আধারে বাড়ী বাড়ী গিয়ে খাদ্যসামগ্রী পৌঁছে দিচ্ছেন এমপি মনোরঞ্জন শীল গোপাল', NULL, NULL, 'uploads/2021-01/News_Photo/1610132645_mp-130.jpg', 'প্রাণঘাতী করোনা ভাইরাস রোধে সাধারণ ছুটিতে গৃহে অবস্থানকারী সাধারণ খেটে খাওয়া মানুষদের রাতে আধারে বাড়ী বাড়ী গিয়ে খাদ্যসামগ্রি (চাল, ডাল, আলু, তেল, সাবান) পৌছে দিচ্ছেন দিনাজপুর-১ আসনের সংসদ সদস্য মনোরঞ্জন শীল গোপাল।\r\n৪ এপ্রিল ২০২০ শনিবার কাহারোল উপজেলার বিভিন্ন ইউনিয়নে রাতে আধারে বাড়ী বাড়ী গিয়ে এই খাদ্যসামগ্রি পৌছে দেন মনোরঞ্জন শীল গোপাল এমপি।\r\nএসময় মনোরঞ্জন শীল গোপাল এমপি বলেন, বর্তমান প্রাণঘাতী করোনা ভাইরাস মোকাবেলায় প্রধানমন্ত্রী শেখ হাসিনা দেশের সকল বিত্তবানদের এগিয়ে আসার আহবান জানিয়েছেন। আর দেশের এই সংকটময় মুহূর্তে সাধারণ মানুষের পাশে দাঁড়ানো আমাদের সবার দায়িত্ব ও কর্তব্য। জননেত্রী শেখ হাসিনা সরকারের প্রতি ও আমার নির্বাচনী এলাকায় যারা আমাকে ভোট দিয়ে আস্থা রেখেছেন তাদের বাড়ি বাড়ি গিয়ে আমরা খাদ্যসামগ্রি বিতরণ করেছি। বর্তমান সময়ে গরীব, মহেনতি মানুষের পাশে দাঁড়ানোর মোক্ষম সময়। প্রধানমন্ত্রী শেখ হাসিনা ক্ষমতায় থাকলে একজন মানুষও না খেয়ে মরবে না। আর আমি চেষ্টা চালিয়ে যাব সর্বদাই আমার নির্বাচনী এলাকার সাধারণ মানুষের পাশে থাকার জন্য।’\r\nতিনি সকলকে সতর্ক করে বলেন, সবাই জানেন অতি সম্প্রতি আবিষ্কৃত হওয়া করোনাভাইরাস একটি বৈশ্বিক দুর্যোগ। পৃথিবীর অধিকাংশ দেশই এখন এই ভাইরাসে আক্রান্ত। এই রোগটি থেকে মুক্তি পেতে হলে আমাদের দৈনন্দিন আচরণ ও চলাফেরাতে পরিবর্তন আনতে হবে। জরুরি প্রয়োজন ছাড়া বাড়ির বাইরে যাওয়া কারোরই উচিত হবে না। মনে রাখতে হবে আমরা যে কেউ যেকোনো সময় এই রোগে আক্রান্ত হতে পারি। কারণ আমরা সবাই এখন এই রোগের ঝুঁকিতে এরয়েছি। তাই প্রয়োজন সচেতনতা ও সতর্কতা। বিশ্ব স্বাস্থ্যসংস্থা চিকিৎসক ও সর্বস্তরের মানুষকেই এই বিষয়ে সচেতন করছেন। শুধু নিজে সতর্ক থাকলেই চলবে না। সবাইকেই সচেতন থাকতে হবে। গুজব থেকে সাবধান থাকার অনুরোধ জানিয়ে তিনি বলেন, এই মুহূর্তে সামাজিক যোগাযোগ মাধ্যমের কোনো গুজবে কান দেবেন না। গুজব ছড়ানোটা বিভ্রান্তিকর ও অপরাধও। তাই গুজবে কান দেন দেবেন না, আতঙ্কিত হবেন না। নিজে সচেতন হোন, অন্যকেও সচেতন হতে বলুন।\r\nএসময় আওয়ামী লীগের নেতৃবৃন্দ উপস্থিত ছিলেন।', NULL, NULL, 0, 1, 1, '2021-01-08 13:04:05', '2021-01-08 13:04:05'),
(26, 1, 13, NULL, NULL, 'বীরগঞ্জে ইয়াবা ট্যাবলেটসহ দুই জন মাদক ব্যবসায়ীকে গ্রেফতার করেছে র‌্যাব', NULL, NULL, 'uploads/2021-01/News_Photo/1610132825_received_870215620400290-660x330.jpeg', 'দিনাজপুরের বীরগঞ্জে ৮৩৫পিস ইয়াবা ট্যাবলেটসহ দুই জন মাদক ব্যবসায়ীকে গ্রেফতার করেছে র‌্যাব।\r\n\r\nসোমবার দুপুর সাড়ে ১২টায় উপজেলার ভোগনগর ইউনিয়নের রহিমবখস দ্বিমুখী উচ্চ বিদ্যালয়ের পূর্ব পার্শ্বে বিদ্যালয়ের সীমানা প্রাচীর সংলগ্ন কাঁচা রাস্তার উপরে অভিযান পরিচালনা করেন র‌্যাব-১৩, সিপিসি-২, নীলফামারী র‌্যাব ক্যাম্পের একটি অভিযানিক দল। অভিযান চালিয়ে ৮৩৫পিস ইয়াবা ট্যাবলেট, মাদক ক্রয়-বিক্রয়ের কাজে ব্যবহৃত ৪টি মোবাইলসেট, ৮টি সীমকার্ড, ১টি মেমোরীকার্ড এবং মাদক বিক্রিত নগত ১২ হাজার ৯০০ টাকা সহ মাদক ব্যবসায়ী উপজেলার মাহাতাবপুর গ্রামের বাবুল হোসেনের ছেলে মো. রুবেল ইসলাম (২২) ও নওগাঁও গ্রামের মোজাম্মেল হকের ছেলে মো. আল আমিন ইসলাম (২০)কে গ্রেফতার করা হয়।\r\n\r\nসিপিসি-২, নীলফামারী ভারপ্রাপ্ত কোম্পানী অধিনায়ক সহকারী পরিচালক মো. হালিউজ্জামান বিষয়টি নিশ্চিত করে জানান, আটককৃতদের প্রাথমিক ভাবে জিজ্ঞাসাবাদে তাহারা মাদক ব্যবসার সাথে জড়িত থাকার কথা স্বীকার করে। পরবর্তীতে উক্ত আসামীদের বীরগঞ্জ থানায় মাদক মামলা রুজু করে থানায় হস্তান্তর', NULL, NULL, 0, 1, 1, '2021-01-08 13:07:05', '2021-01-08 13:07:05'),
(27, 1, 2, NULL, NULL, 'দেশে এখন করোনার দ্বিতীয় ঢেউ চলছে -মনোরঞ্জন শীল গোপাল এমপি', NULL, NULL, 'uploads/2021-01/News_Photo/1610133704_FB_IMG_1607083610595-660x330.jpg', 'দিনাজপুর-১ আসনের সংসদ সদস্য মনোরঞ্জন শীল গোপাল বলেছে, দেশে এখন করোনার দ্বিতীয় ঢেউ চলছে। করোনা থেকে বাঁচতে হলে সবাইকে স্বাস্থ্যবিধি মানতে ও মাস্ক পরিধান করতে হবে। আমাদের দেশের সাধারণ মানুষ অনেকেই এখনও স্বাস্থ্যবিধি ঠিকমত মানছেন না। এভাবে যদি চলতে থাকে তাহলে আমাদের দেশের অবস্থা আরও ভয়াবহ হবে। তিনি বলেন, ভ্যাকসিন না আসা পর্যন্ত মাস্কই হচ্ছে করোনা প্রতিরোধের সবচেয়ে বড় হাতিয়ার। সেই সঙ্গে আমাদের সকলকে নিয়ম মতো হাত ধুতে হবে এবং সামাজিক দূরত্ব বজায় রাখতে হবে।\r\n৩ ডিসেম্বর ২০২০ বৃহস্পতিবার কাহারোল উপজেলা পরিষদ চত্বরে কোভিড-১৯ প্রাদুর্ভাব ও অভাবকালী সময়ে প্রকল্প উপকার ভোগীদের মাঝে খাদ্য ও স্বাস্থ্য সহায়তা কার্যক্রমের উদ্বোধনকালে মনোরঞ্জন শীল গোপাল এমপি এসব কথা বলেন।\r\nকাহারোল উপজেলা পরিষদ চত্বরে বিএমজেড ও নেটজ এর অর্থায়নে বাংলাদেশর কাঠামোগত ভাবে পিছিয়ে পড়া মানুষের অতিদারিদ্র্য ও ক্ষুধা দূরীকরণ (দাবি) প্রকল্প পল্লীশ্রী এর আয়োজনে উপজেলা প্রশাসন তত্ত্বাবধানে কোভিড-১৯ প্রাদুর্ভাব ও অভাবকালী সময়ে প্রকল্পে ৪শ জন উপকার ভোগীদের মাঝে খাদ্য ও স্বাস্থ্য সহায়তা প্রদান করা হয়। এতে প্রত্যেক পরিবার পায় ২৫ কেজি চাল, ২ কেজি মসুর ডাল, ২ কেজি চিনি, ১ কেজি সুজি, ২লিটার সোয়াবিন তেল, ২টি সাবান, ৩টি মাস্ক।\r\nএসময় উপস্থিত ছিলেন উপজেলা নির্বাহী অফিসার মো. মনিরুল হাসান, উপজেলা পরিষদের চেয়ারম্যান আব্দুল মালেক সরকার, উপজেলা আওয়ামী লীগের সভাপতি একেএম ফারুক, কাহারোল থানার ওসি মো. ফেরদৌস হোসেন, দিনাজপুর পল্লশ্রী ঋণ ও আয়বৃদ্ধি মুলক কর্মসূচীর ম্যানেজার সৈয়দ মোস্তফা কামালসহ অন্যান্য নেতৃবৃন্দ।', NULL, NULL, 11, 1, 1, '2021-01-08 13:21:44', '2021-01-21 03:58:04'),
(28, 1, 5, NULL, NULL, 'বীরগঞ্জে খাদ্যের নিরাপদতা শীর্ষক সেমিনার', NULL, NULL, 'uploads/2021-01/News_Photo/1610133811_received_443244470418935-660x330.jpeg', 'দিনাজপুরের বীরগঞ্জে মুজিব বর্ষের কোভিট-১৯ এর স্বাস্থ্যবিধি অনুসরণ করে উপজেলা পর্যায়ে জনসচেতনতা সৃষ্টির লক্ষ্যে খাদ্যের নিরাপদতা শীর্ষক সেমিনার অনুষ্ঠিত হয়েছে।\r\n\r\nবৃহস্পতিবার সকাল ১১টায় বাংলাদেশ নিরাপদ খাদ্য কর্তৃপক্ষের আয়োজনে বীরগঞ্জ উপজেলা প্রশাসনের সহযোগিতায় উপজেলা পরিষদ সভা কক্ষে উক্ত সেমিনার অনুষ্ঠিত হয়। উপজেলা নির্বাহী অফিসার মোঃ আব্দুল কাদেরের সভাপতিত্বে সেমিনারে প্রধান অতিথি হিসেবে বক্তব্য রাখেন সাবেক সংসদ সদস্য ও উপজেলা চেয়ারম্যান মোঃ আমিনুল ইসলাম।\r\n\r\nবিশেষ অতিথি হিসেবে বক্তব্য রাখেন জেলা নিরাপদ খাদ্য অফিসার মোঃ মুসফিকুর রহমান, উপজেলা কৃষি কর্মকর্তা আবু রেজা মোঃ\r\nআসাদুজ্জামান, উপজেলা শিক্ষা অফিসার মোঃ আবুল কালাম আজাদ, উপজেলা খাদ্য নিয়ন্ত্রক ফিরোজ আহমেদ ওসমান, স্যানিটারি ইন্সপেক্টর মোঃ ফরিদ বিন ইসলাম, সহকারী উপজেলা মৎস্য কর্মকর্তা মোছাঃ জোৎন্সা আরা, সাতোর ইউনিয়ন পরিষদ চেয়ারম্যান মোঃ রেজাউল করিম শেখ, জেলা নিরাপদ খাদ্য নমুনা সংগ্রহ অফিসার মোঃ হারুন অর রশিদ আকন্দ প্রমুখ।এ সময় উপজেলা প্রশাসনের বিভিন্ন দপ্তরের কর্মকর্তা, শিক্ষক,\r\nসাংবাদিক, ব্যবসায়ীগণ উপস্থিত ছিলেন।', NULL, NULL, 1, 1, 1, '2021-01-08 13:23:31', '2021-01-20 23:39:02'),
(29, 1, 5, NULL, NULL, 'বীরগঞ্জে আসন্ন মোহনপুর ইউপি নির্বাচনে নৌকা প্রতীক পেতে সমর্থন চেয়ে মতবিনিময় সভা', NULL, NULL, 'uploads/2021-01/News_Photo/1610133989_IMG_20210106_203704-660x330.jpg', 'দিনাজপুরের বীরগঞ্জ উপজেলার ১০নং মোহনপুর ইউপির আসন্ন নির্বাচনে নৌকা প্রতীক পেতে সকলের সমর্থন চেয়ে মতবিনিময় সভা অনুষ্ঠিত হয়েছে।\r\n\r\nবুধবার সন্ধ্যায় মাটিয়াকুড়া উচ্চ বিদ্যালয় মাঠে মোহনপুর ইউনিয়ন আওয়ামীলীগের সভাপতি মোঃ তাইজুল ইসলামের আহ্বানে ও ৭নং ওয়ার্ডবাসীর আয়োজনে বিশিষ্ট ব্যবসায়ী আলহাজ্ব রফিকুল্লাহ এর সভাপতিত্বে মতবিনিময় সভায় বক্তব্য রাখেন সাবেক ইউপি চেয়ারম্যান মোঃ বদিউজ্জামান বৈদা, ইউনিয়ন আওয়ামীলীগের সহ-সভাপতি আমিনুল ইসলাম, সাবেক মেম্বার ডাঃ নজরুল ইসলাম, আলহাজ্ব ফেরাজ উদ্দিন, আলহাজ্ব আঃ সাত্তার, খোশবুল ইসলাম মাস্টার, মোফাজ্জল মাস্টার, ইব্রাহীম, আঃ মালেক, আইয়্যুব আলী, ওমর ফারুক, মোঃ মুছা, রুহুল আমীন, জামাল উদ্দিন, ফজলুল করিম প্রমুখ। মতবিনিময়ের আলোচনা সভায় ইউনিয়ন আওয়ামীলীগের বহুল পরিচিত, বিভিন্ন সময়ে দলীয় আন্দোলন-সংগ্রামের অগ্রপথিক, এই এলাকার গনমানুষের পরীক্ষিত প্রবীন নেতা মোঃ তাইজুল ইসলাম নিজেকে আসন্ন ইউপি নির্বাচনে চেয়ারম্যান পদপ্রার্থী হিসেবে ঘোষণা করে দলীয় মনোনয়নে নৌকা প্রতীক পেতে দল মত নির্বিশেষে সকল শ্রেনী-পেশার মানুষের সমর্থন ও দোয়া চেয়ে নির্বাচনী প্রচারণার আনুষ্ঠানিকভাবে ঘোষণা দেন। এসময় ওয়ার্ড আওয়ামী লীগের সভাপতি ওবায়দুল ইসলাম, সাধারণ সম্পাদক নুর আমিন,অঙ্গ সংগঠনের সকল নেতৃবৃন্দসহ দল মত নির্বিশেষে ৭নং ওয়ার্ডের কয়েকশত সাধারণ মানুষ উপস্থিত থেকে ও হাত উত্তোলন করে তাদের জনপ্রিয় নেতা তাইজুল ইসলামকে সমর্থন জানান।', NULL, NULL, 2, 1, 1, '2021-01-08 13:26:29', '2021-01-20 23:39:12'),
(30, 1, 9, NULL, NULL, 'লক্ষাধিক মানুষের দায়িত্ব নিলেন হৃত্বিক', NULL, NULL, 'uploads/2021-01/News_Photo/1610134098_IMG_20200409_120415-651x330.jpg', 'বলিউড অভিনেতা হৃত্বিক রোশন নিজের ফিটনেস ও ব্যক্তিত্বের কারণে কোটি কোটি ভক্তের হৃদয় জয় করে নিয়েছেন। এবার তিনি ঘোষণা দিয়েছেন করোনার দুর্যোগ চলাকালীন সময় পরিস্থিতি স্বাভাবিক না হওয়ার পর্যন্ত এক লাখেরও বেশি মানুষের খাবারের দায়িত্ব নিবেন।\r\n\r\nভারতে লকডাউন চলাকালীন দৈনিক রোজগারে শ্রমিক ও বৃদ্ধাশ্রমের অসহায় মানুষ ও খেটে খাওয়া মানুষদের পাশে থাকবেন এই অভিনেতা।\r\n\r\nকরোনা মোকাবিলায় অক্ষয় পাত্র নামে একটি স্বেচ্ছাসেবী সংস্থার সঙ্গী হয়েছেন হৃত্বিক রোশন। ওই সংস্থার সঙ্গে হাত মিলিয়েই প্রতিদিন প্রায় এক লাখ ২০ হাজার মানুষের কাছে খাবার পৌঁছে দেবেন তিনি।\r\n\r\nনিজেদের টুইটার হ্যান্ডেলে হৃত্বিকের প্রতি কৃতজ্ঞতা জানিয়ে টুইট করেছে অক্ষয় পাত্র নামে ওই সংস্থা। শ্রমিক পরিবারগুলোর পাশাপাশি বৃদ্ধাশ্রমের মানুষেরও যাতে খাবারের কষ্ট না হয়, এবার সেই দায়িত্বও নিচ্ছেন হৃত্বিক। হৃত্বিকের এমন উদ্যোগে খুশি হয়ে তাকে ভালোবাসা জানাচ্ছেন তার ভক্তরা।', NULL, NULL, 1, 1, 1, '2021-01-08 13:28:18', '2021-01-20 23:44:03'),
(31, 1, 8, NULL, NULL, 'লিবিয়ায় ২৬ বাংলাদেশিকে গুলি করে হত্যা', NULL, NULL, 'uploads/2021-01/News_Photo/1610134195_09-37-15-rd-768x432-1-660x330.jpg', 'লিবিয়ার দক্ষিণাঞ্চলীয় শহর মিজদাহতে ২৬ বাংলাদেশিসহ ৩০ অভিবাসীকে গুলি করে হত্যা করেছে হয়েছে লিবিয়ান মিলিশিয়া বাহিনী।\r\n\r\nবৃহস্পতিবার (২৮ মে) লিবিয়ার রাজধানী ত্রিপলি থেকে ১৮০ কি.মি. দক্ষিণে মিজদাহ শহরে ওই অভিবাসীকে নির্বিচারে গুলি করে হত্যা করা হয়।\r\n\r\nলিবিয়ায় নিযুক্ত বাংলাদেশ দূতাবাস জানিয়েছে, লিবিয়ার মিলিশিয়া বাহিনীর এলোপাতাড়ি গুলিতে ২৬ বাংলাদেশিসহ ৩০ অভিবাসী নিহত হন। এ ঘটনায় প্রাণে বেঁচে যাওয়া এক বাংলাদেশির সঙ্গে টেলিফোনে যোগাযোগ হয়েছে দূতাবাসের।\r\n\r\nকোনোভাবে প্রাণে বেঁচে বর্তমানে একজন লিবিয়ানের আশ্রয়ে আত্মগোপন করে আছেন ওই বাংলাদেশি। সেখান থেকে তিনি দূতাবাসকে জানান, ১৫ দিন আগে বেনগাজী থেকে মরুভূমি পারি দিয়ে কাজের সন্ধানে মানব পাচারকারীরা তাদের লিবিয়ার ত্রিপলি শহরে নিয়ে আসার পথে তিনিসহ মোট ৩৮ বাংলাদেশি মিজদাহ শহরে দুষ্কৃতিকারীদের হাতে জিম্মি হন।\r\n\r\nজিম্মি অবস্থায় তাদের অত্যাচার, নির্যাতন করার একপর্যায়ে অপহৃত ব্যক্তিরা মূল অপহরণকারী লিবিয়ান ব্যক্তিকে হত্যা করেন এবং এর জের হিসেবে অন্যান্য দুষ্কৃতিকারীরা আকস্মিকভাবে তাদের ওপর এলোপাতাড়ি গুলি করে। এতে ২৬ বাংলাদেশি নিহত হয়, যাদের মরদেহ মিজদাহ হাসপাতালে রাখা হয়েছে। আহত অন্যান্য বাংলাদেশিরা হাতে-পায়ে, বুকে-পিঠে গুলিবিদ্ধ হয়ে হাসপাতালে চিকিৎসাধীন রয়েছেন।\r\n\r\nএ ঘটনায় আহত ১১ বাংলাদেশিকে জিনতান হাসপাতালে প্রাথমিক চিকিৎসা দিয়ে উন্নত চিকিৎসার জন্য ত্রিপলি মেডিকেল সেন্টারে (টিএমসি) পাঠানোর ব্যবস্থা নেওয়া হয়েছে।\r\n\r\nলিবিয়ার আন্তর্জাতিক অভিবাসন সংক্রান্ত মুখপাত্র সাফা মেশেলি বলেন, আমরা সদ্যই খবরটি জেনেছি এবং আরও বিশদ জানতে চেষ্টা করছি। বর্তমানে আমরা বেঁচে যাওয়া লোকদের সহায়তা করছি।\r\n\r\nএ ব্যাপারে পররাষ্ট্রমন্ত্রী ড. এ কে আবদুল মোমেন জানিয়েছেন, বাংলাদেশ দূতাবাসের পক্ষ থেকে এক কর্মকর্তাকে হাসপাতালে পাঠানো হচ্ছে।', NULL, NULL, 2, 1, 1, '2021-01-08 13:29:55', '2021-01-20 23:43:57');
